import BlackSection from './components/BlackSection.vue';
import WhiteSection from './components/WhiteSection.vue';
import VideoCV from './components/VideoCV.vue';
import EndSection from './components/EndSection.vue';
export default {
  name: 'App',
  components: {
    BlackSection,
    WhiteSection,
    VideoCV,
    EndSection
  },
  data() {
    return {
      images: ['./assets/images/child-labour-poster.jpg', './assets/images/longhornbbq-poster_1.jpg'],
      logos: ['./assets/images/child labour logo.png', './assets/images/longhornbbqlogojpg.jpg'],
      websiteImages: ['./assets/images/website2.png', './assets/images/website3.png', './assets/images/website4.png', './assets/images/website5.png', './assets/images/website6.png', './assets/images/website.png'],
      pdfUrl: './assets/cv/portfolio-cv.pdf',
      currentIndex: 0,
      currentIndexLogos: 0,
      currentIndexWebsite: 0,
      intervalId: null,
      intervalIdLogos: null,
      intervalIdWebsite: null
    };
  },
  mounted() {
    window.addEventListener('scroll', this.onScroll);
    this.startSlideshow();
    this.startLogosSlideshow();
    this.startWebsiteSlideshow();
    this.startAutoplay();
  },
  methods: {
    isInView(element) {
      const bounding = element.getBoundingClientRect();
      return bounding.top >= 0 && bounding.bottom <= (window.innerHeight || document.documentElement.clientHeight);
    },
    onScroll() {
      const images = document.querySelectorAll('.fade-in-image');
      const line = document.querySelector('.line');
      const nextLine = document.querySelector('.next_line');
      line.classList.add('animate');
      nextLine.classList.add('animate');
      images.forEach(image => {
        if (this.isInView(image)) {
          image.classList.add('active');
        }
      });
    },
    mounted() {},
    next() {
      this.currentIndex = (this.currentIndex + 1) % this.images.length;
    },
    startAutoplay() {
      this.autoplayInterval = setInterval(() => {
        this.next();
      }, this.autoplayDelay);
    },
    stopAutoplay() {
      clearInterval(this.autoplayInterval);
    }
  },
  beforeUnmount() {
    this.stopAutoplay();
  }
};
